async function safeLLMCall<T>(
  fn: () => Promise<T>,
  options: { retries?: number; fallback?: T }
): Promise<T> {
  const { retries = 3, fallback } = options;

  for (let i = 0; i < retries; i++) {
    try {
      return await fn();
    } catch (error) {
      if (error.status === 429) {
        // é€Ÿç‡é™åˆ¶ - æŒ‡æ•°é€€é¿
        await sleep(Math.pow(2, i) * 1000);
        continue;
      }
